import React, { useState } from 'react';
import { useForm } from 'react-hook-form';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Register.css';

function Register() {
  const { register, handleSubmit, formState: { errors } } = useForm();
  const [showPassword, setShowPassword] = useState(false); 
  const navigate = useNavigate();

  const onSubmit = async (data) => {
    try {
      const response = await axios.post('http://localhost:3000/user/register', data);
      alert(response.data.message); // Success message
    } catch (error) {
      alert(error.response?.data?.message || 'Registration failed'); // Error message
    }
  };

  return (
    <div className="register-container">
      <h2>Register</h2>
      <form onSubmit={handleSubmit(onSubmit)} className="register-form">
        <div>
          <label>Email:</label>
          <input
            type="email"
            {...register('email', { required: 'Email is required', pattern: /^[^\s@]+@[^\s@]+\.[^\s@]+$/ })}
          />
          {errors.email && <p className="error-message">{errors.email.message}</p>}
        </div>
        
        <div>
          <label>Password:</label>
          <div className='password-input-container'>
            <input
              type={showPassword ? 'text' : 'password'}
              {...register('password', { required: 'Password is required', minLength: { value: 6, message: 'Password must be at least 6 characters long' } })}
            />
            <span 
              className='password-toggle-icon'
              onClick={() => setShowPassword(!showPassword)}
            >
              {showPassword ? 'üëÅÔ∏è' : 'üôà'}
            </span>
          </div>
          {errors.password && <p className="error-message">{errors.password.message}</p>}
        </div>
        
        <button type="submit" className="register-button">Register</button>
      </form>
      <div className="navigation-buttons">
        <button onClick={() => navigate('/')} className="home-button">Home</button>
        <button onClick={() => navigate('/login')} className="login-button">Already have an account?Login!</button>
      </div>
    </div>
  );
}

export default Register;
